import classes from './Cart.module.css';
import Modal from '../UI/Modal';
import { useContext } from 'react';
import CartItem from './CartItem';
import Cartcontext from '../../store/cart-context';


const items=[ 
    {
      id: 'c1',
      name: 'Sushi',
      amount: 2,
      price: 29.99,
    }
];
const Cart =props=>{
    const crtCtx = useContext(Cartcontext);
    const totalAmount =`$${crtCtx.totalAmount}`;
    const hasItems = crtCtx.items.length>0;
    const cartItemRemoveHandler = id=>{};
    const cartItemHandler = item =>{};
    const cartItems = (<ul className={classes['cart-items']}>
                    {crtCtx.items.map(item =>(<CartItem key ={item.id} name ={item.name} amount={item.amount} price={item.price} onRemove ={cartItemRemoveHandler} onAdd ={cartItemHandler}/>))}
                   </ul>); 
    return(
        <Modal onClose={props.onClose}> 
            {cartItems}
            <div className={classes.total}>
                <span>Total Amount</span>
                <span>{totalAmount}</span>
            </div>
            <div className={classes.actions}>
            <button className={classes['button-alt']} onClick={props.onClose}>Close</button>
            {hasItems && <button className={classes.button}>Order</button>}
            </div>
        </Modal>
    );
}

export default Cart;
