import { useContext, useEffect, useState } from "react";
import CartIcon from "../Cart/CartIcon";
import styles from './HeaderCartButton.module.css';
import Cartcontext from "../../store/cart-context";

const HeaderCartButton = props =>{
    const [buttonH, setButttonH]=useState(false);
    const cartCtx = useContext(Cartcontext);
    const numberofCartItems = cartCtx.items.reduce((current,item)=>{
        return current+item.amount},0);
    const btnclass = `${styles.button}${styles.bump}`;

    useEffect(()=>{
        setButttonH(true);
    },[]);
    
    return(
        <button className={numberofCartItems?btnclass:styles.button} onClick ={props.onClick}>      
            <span className={styles.icon}>
                <CartIcon/></span>
            <span>Your Cart</span>
            <span className={styles.badge}>{numberofCartItems}</span>
        </button>
    );
};

export default HeaderCartButton;
